    //CALCULA O IMC
    private void calculaImc(float peso, float altura) {
        float imc;
        // FAZ O CÁLCULO
        imc = peso / (altura * altura);

        //ESCREVE NA TELA
        setResultado(imc);
        interpretaIMC(imc);
    }

    //INTERPRETA O IMC
    private void interpretaIMC(float imc) {

        String mensagem = "";

        if (imc < 18.5) {

            mensagem = "Magreza";

        } else if (imc > 18.5 && imc <= 24.9) {

            mensagem = "Normal";

        } else if (imc > 25 && imc <= 29.9) {

            mensagem = "Sobrepeso";

        } else if (imc > 30 && imc <= 39.9) {

            mensagem = "Obesidade";

        } else if (imc >= 40) {

            mensagem = "Obesidade grave";

        }

        setInterpretacao(mensagem);
    }

    private void setResultado(Float resultado) {
        //DEFINE A FORMATAÇÃO DO FLOAT
        DecimalFormat df = new DecimalFormat("####0.00");
        //SETA O TEXTO
        txtResultado.setText(df.format(resultado));
    }

    private void setInterpretacao(String interpr) {
        txtInterpr.setText(interpr);
    }

